function getLoadMatrix(T, PMax)
    L = length(PMax)
    m = Matrix{Float64}(undef, T, L)
    for l = 1:L
        curve = rand(Curves)
        _PMax = PMax[l]
        for t = 1:T
            m[t, l] = clamp(curve[t] + rand(_ND), 0., 1.)_PMax
        end
    end
    m
end
_zg(f, Zone, N) = [[f(z,g) for g=eachindex(N[z])] for z=Zone]
_slope(Zone, N)                     = _zg((_,_) -> 1/rand(2:6), Zone, N)
_knee(Zone, N)                      = _zg((_,_) -> rand(70:85)/100, Zone, N)
_mul(Zone, N)                       = _zg((_,_) -> rand(12:18)/10, Zone, N)
_ramp(Zone, N, PMax)                = _zg((z,g) -> PMax[z][g]/rand(1:8), Zone, N)
_rCost(Zone, N, U::UnitRange{Int})  = _zg((_,_) -> 1/rand(U), Zone, N)

_other() = (
    Zone = Zone.Other,
    N = N.Other,
    PMax = PMax.Other,
    Cost = (
        p = (
            Slope = _slope(Zone.Other, N.Other),
            Knee = _knee(Zone.Other, N.Other),
            Mul = _mul(Zone.Other, N.Other)
        ),
    )
)
_reserve() = (
    Zone = Zone.Reserve,
    N = N.Reserve,
    PMax = PMax.Reserve,
    Ramp = Dict(
        0 => _ramp(Zone.Reserve, N.Reserve, PMax.Reserve),
        1 => _ramp(Zone.Reserve, N.Reserve, PMax.Reserve)
    ),
    Cost = (
        p = ( # to p0
            Slope = _slope(Zone.Reserve, N.Reserve),
            Knee = _knee(Zone.Reserve, N.Reserve),
            Mul = _mul(Zone.Reserve, N.Reserve)
        ),
        r = Dict( # to δ0
            0 => _rCost(Zone.Reserve, N.Reserve, 5:9),
            1 => _rCost(Zone.Reserve, N.Reserve, 3:7)
        )
    )
)
_load(T) = (
    Zone = Zone.Load,
    N = N.Load,
    PMax = PMax.Load,
    P = [getLoadMatrix(T, PMax.Load[z]) for z=Zone.Load]
)

_P() = Vector{Matrix{Float64}}(undef, maximum(Zone.Demand))
function _P(T)
    v = _P()
    for z=Zone.Demand
        v[z] = getLoadMatrix(T, PMax.Demand[z])
    end
    v
end
function _ζCost(T)
    v = _P()
    for z=Zone.Demand
        v[z] = rand(10 ./ (5:9), T, length(N.Demand[z]))
    end
    v
end

_demand(T) = (
    Zone = Zone.Demand,
    N = N.Demand,
    PMax = PMax.Demand,
    P = _P(T),
    Cost = (
        p = Dict(
            0 => _ζCost(T), # DR: make load goes down
            1 => _ζCost(T)  # DR: make load goes up
        ),
    )
)
get_case2383(T) = (_other(), _reserve(), _demand(T), _load(T)) # Other, Reserve, Demand, Load = LoadGen.get_case2383(T)

end

